settings.custom_css:
selector .elementor-divider-separator {
    border-width: 1px 0px 0px 0px;
    border-style: solid;
    border-image: linear-gradient(to right, #ff000000, #A200C2, #ff000000) 1;
}

selector .elementor-divider-separator {
    border-width: 1px 0px 0px 0px;
    border-style: solid;
    border-image: linear-gradient(to right, #ff000000, #A200C2, #ff000000) 1;
}

selector {
  z-index:0;
      backdrop-filter: blur( 8px );
    -webkit-backdrop-filter: blur( 8px );
    border-radius:15px;
    margin:5px;
}

selector:before {
  content:"";
  position:absolute;
  z-index:-1;
  top:0;
  left:0;
  right:0;
  bottom:0;
  padding: 2px;
  border-radius: 15px;
  background: linear-gradient(to top, #A200C2, #ff000000);
  -webkit-mask: 
     linear-gradient(#fff 0 0) content-box, 
     linear-gradient(#fff 0 0);
          mask: 
     linear-gradient(#fff 0 0) content-box, 
     linear-gradient(#fff 0 0);
  -webkit-mask-composite: xor;
  mask-composite: exclude;
}

